    private void buttonResultadoMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_buttonResultadoMouseClicked

       r2 = r.split(tipo)[0];
       r3 = r.split(tipo)[1];
       metodo(tipo, r2, r3);
       
       jLabel2.setText(""+r+" =");
       
       r = Double.toString(c.getResultado());
       jTextField1.setText(""+r);
       ve = false;
    }//GEN-LAST:event_buttonResultadoMouseClicked

