    public void Iniciar(){
        jTextField1.setText("0");
        r = null;
    }
    
    public String Mostrar(String r) {
        if(r != null){
            jTextField1.setText(""+r);
            
        }
        return r;
    }
    
    public void colocarNumero(String v){
        
        if(r == null){
            r = v;
        }
        else{
            if(r.length() < 8 && ve == false && r != "ERRO"){
                r = r + v;
            }
            else if(r.length() <= 8 && ve == true){
                r = r + v;
            }
            else if(ve == true && r != "ERRO"){
                r2 = r.split(tipo)[0];
                r3 = r.split(tipo)[1]; 
               if(r3.length() <= 8 && r != "ERRO"){
                   r = r + v;
               }
               else{
                   r = "ERRO";
               }
            }
        }
        
    }
    
    public void metodo(String tipo, String r2, String r3){
        
        if(tipo == "x"){
            c.multiplicar(r2, r3);
        }else if(tipo == "\\+"){
           c.somar(r2, r3);
        }else if(tipo == "/"){
            c.dividir(r2, r3);
        }else if(tipo == "-"){
            c.subtrair(r2, r3);
        }
    }
    
    public void apagarTexto(){
        if(r != null){
            r =  r.substring (0, r.length() - 1);
        }
    }
    
